create table message (id int8 generated by default as identity, amiauthor boolean, content varchar(255), is_read boolean not null, recipient varchar(255), scribe_id int8, sender varchar(255), time timestamp, unread_count_messages int4 not null, user_messages_id int8, primary key (id))
;create table message_file_ids (message_id int8 not null, file_ids int8)
;