create table job_seeker_table (id int8 generated by default as identity, about varchar(255), address varchar(255), birthday date, city varchar(255), country varchar(255), email varchar(255), firstname varchar(255), is_favorite int8, lastname varchar(255), password varchar(255), phone_number varchar(255), rol varchar(255), status_of_job_seeker varchar(255), user_application_date timestamp, experience_id int8, image_id int8, resume_id int8, primary key (id))
;